mapper.select:
select 
  *
from
  music_member
 where user_id = #{user_id}
       and user_pwd = #{user_pwd}

update music_member 
        set user_purchase = 1 
        where user_no = #{user_no};

update music_member 
        set user_purchase = 2 
        where user_no = #{user_no};

update music_member 
        set user_purchase = 3 
        where user_no = #{user_no};

update music_member 
        set user_purchase = 4 
        where user_no = #{user_no};

update music_member 
        set user_purchase = 5 
        where user_no = #{user_no};

mapper.update:
update music_member set user_email = #{user_email},
	user_phone = #{user_phone}, user_update = sysdate
	where user_no = #{user_no}
